import React, { useState, useEffect, useRef } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';
import { Card, Spinner, Alert, Button, Container, ListGroup } from 'react-bootstrap';
import Editor from '@monaco-editor/react';
import { toast } from 'react-hot-toast';
import { CopyToClipboard } from 'react-copy-to-clipboard';
import { FaPlus, FaPlayCircle, FaCheckCircle, FaTimesCircle, FaCopy } from 'react-icons/fa';

const SnippetPage = ({ theme }) => {
  const { id } = useParams();
  const [snippet, setSnippet] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  
  const editorRef = useRef(null);
  const monacoRef = useRef(null);
  const [analysis, setAnalysis] = useState([]);
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  
  useEffect(() => {
    // ... (This hook is unchanged)
    const fetchSnippet = async () => {
      try {
        setLoading(true);
        setError('');
        const response = await axios.get(`http://localhost:5000/api/snippets/${id}`);
        setSnippet(response.data);
      } catch (err) {
        setError(err.response?.data?.message || 'Snippet not found.');
      } finally {
        setLoading(false);
      }
    };
    fetchSnippet();
  }, [id]);

  function handleEditorDidMount(editor, monaco) {
    editorRef.current = editor;
    monacoRef.current = monaco;
  }

  // --- UPDATED Helper Function ---
  // This now formats errors from py_compile
  const processMarkers = (markers, isPython = false) => {
    const monaco = monacoRef.current;
    
    if (markers.length === 0) {
      setAnalysis([{ 
        type: 'success', 
        message: 'No errors or warnings found. This code looks clean!' 
      }]);
    } else {
      const formattedSuggestions = markers.map(marker => {
        
        // This formats errors from the Monaco editor (JS, HTML, etc.)
        if (!isPython) {
          let customSuggestion = 'Review the error and your code logic.';
          if (marker.message.includes("';' expected")) {
            customSuggestion = `Suggestion: Try adding a semicolon ';' at the end of line ${marker.startLineNumber - 1}.`;
          }
          if (marker.message.includes("is not defined")) {
            customSuggestion = `Suggestion: Make sure the variable or function "${marker.message.split("'")[1]}" is defined, imported, and spelled correctly.`;
          }
          if (marker.message.includes("Expected closing tag")) {
            const tagName = marker.message.split("'")[1];
            customSuggestion = `Suggestion: The <${tagName}> tag on line ${marker.startLineNumber} is not closed. Try adding a </${tagName}> tag.`;
          }
          return {
            type: marker.severity === monaco.MarkerSeverity.Error ? 'danger' : 'warning',
            line: marker.startLineNumber,
            message: marker.message,
            suggestion: customSuggestion
          };
        } 
        
        // --- NEW: This formats errors from our py_compile API ---
        else {
          let customSuggestion = `Suggestion: ${marker.message}.`;
          
          if (marker.message.includes("Invalid syntax")) {
             customSuggestion = `Suggestion: ${marker.message}. Check for typos (like 'defs' instead of 'def'), missing colons, or incorrect indentation.`;
          }
          
          return {
            type: 'danger', // py_compile only finds syntax errors
            line: marker.line,
            message: marker.message,
            suggestion: customSuggestion
          };
        }
      });
      setAnalysis(formattedSuggestions);
    }
    setIsAnalyzing(false);
  };

  // --- This "Analyze" function is unchanged from the last step ---
  const handleAnalyzeCode = async () => {
    if (!editorRef.current || !monacoRef.current) return;
    
    setIsAnalyzing(true);
    setAnalysis([]);
    
    const language = snippet.language;
    
    // --- ROUTE 1: PYTHON ANALYSIS (Back-end) ---
    if (language === 'python') {
      try {
        const response = await axios.post('http://localhost:5000/api/analyze/python', {
          code: snippet.content
        });
        // Send the results from our API to be formatted
        processMarkers(response.data, true);
      } catch (err) {
        setAnalysis([{ type: 'danger', message: 'Error running Python analyzer on server.' }]);
        setIsAnalyzing(false);
      }
    } 
    
    // --- ROUTE 2: MONACO ANALYSIS (Front-end) ---
    else {
      const monaco = monacoRef.current;
      const model = editorRef.current.getModel();
      
      editorRef.current.focus(); // Wake up the linter
      
      let attempts = 0;
      const maxAttempts = 10;
      const pollInterval = 300; 

      const pollForMarkers = () => {
        const markers = monaco.editor.getModelMarkers({ resource: model.uri });
        if (markers.length > 0 || attempts >= maxAttempts) {
          processMarkers(markers, false);
        } else {
          attempts++;
          setTimeout(pollForMarkers, pollInterval);
        }
      };
      
      pollForMarkers();
    }
  };
  
  const onCopyCode = () => {
    toast.success('Code copied to clipboard!');
  };

  // ... (Loading and Error states are unchanged)
  if (loading) {
    return (
      <Container className="text-center mt-5">
        <Spinner animation="border" />
        <p>Loading Snippet...</p>
      </Container>
    );
  }
  if (error) {
    return (
      <Container className="mt-5">
        <Alert variant="danger"><h4>Error</h4><p>{error}</p><Button as={Link} to="/" variant="danger">Go Home</Button></Alert>
      </Container>
    );
  }

  return (
    <Container fluid className="mb-4">
      <Button as={Link} to="/" variant="secondary" className="mb-3">
        <FaPlus className="me-2" />
        Create New Snippet
      </Button>
      
      {snippet && (
        <Card className="editor-card">
          <Card.Header>
            Language: <strong>{snippet.language}</strong> | Created: {new Date(snippet.createdAt).toLocaleString()}
          </Card.Header>
          <Card.Body>
            <Editor
              height="60vh"
              theme={theme === 'dark' ? 'vs-dark' : 'vs-light'}
              language={snippet.language}
              value={snippet.content}
              onMount={handleEditorDidMount}
              options={{
                readOnly: true,
                domReadOnly: true,
                minimap: { enabled: true },
                validate: true 
              }}
            />
          </Card.Body>
          
          <Card.Footer>
            <div className="d-flex align-items-center">
              <Button variant="success" onClick={handleAnalyzeCode} className="py-2" disabled={isAnalyzing}>
                {isAnalyzing ? (
                  <>
                    <Spinner as="span" animation="border" size="sm" role="status" aria-hidden="true" />
                    <span className="ms-2">Analyzing...</span>
                  </>
                ) : (
                  <>
                    <FaPlayCircle className="me-2" />
                    Analyze Code
                  </>
                )}
              </Button>

              <CopyToClipboard text={snippet.content} onCopy={onCopyCode}>
                <Button variant="primary" className="ms-2 py-2">
                  <FaCopy className="me-2" /> Copy Code
                </Button>
              </CopyToClipboard>
            </div>
            
            {analysis.length > 0 && (
              <div className="mt-4">
                <h4>Analysis Results:</h4>
                <ListGroup className="analysis-list">
                  {analysis.map((item, index) => (
                    <ListGroup.Item 
                      key={index} 
                      className={theme === 'light' ? 'bg-light text-dark' : 'bg-dark text-light'}
                    >
                      {item.type === 'success' ? (
                        <strong className="text-success"><FaCheckCircle className="me-2" />{item.message}</strong>
                      ) : (
                        <>
                          <strong className={item.type === 'danger' ? 'text-danger' : 'text-warning'}>
                            <FaTimesCircle className="me-2" /> Line {item.line}:
                          </strong> {item.message}
                          <br />
                          <small>{item.suggestion}</small>
                        </>
                      )}
                    </ListGroup.Item>
                  ))}
                </ListGroup>
              </div>
            )}
          </Card.Footer>
        </Card>
      )}
    </Container>
  );
};

export default SnippetPage;
