import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom';
import { Container, Navbar, Form } from 'react-bootstrap';
import './App.css';

// 1. Import icons for the logo and theme toggle
import { FaCode, FaSun, FaMoon } from 'react-icons/fa';

import HomePage from './components/HomePage';
import SnippetPage from './components/SnippetPage';

function App() {
  // 2. Set up the theme state. Default to 'dark'
  const [theme, setTheme] = useState('dark');

  // 3. This function toggles the theme
  const toggleTheme = () => {
    setTheme(theme === 'light' ? 'dark' : 'light');
  };

  // 4. This effect applies the theme class to the <body>
  useEffect(() => {
    document.body.className = ''; // Clear existing classes
    document.body.classList.add(theme); // Add the current theme
  }, [theme]);

  return (
    <Router>
      {/* 5. Pass the correct Bootstrap theme to the Navbar */}
      <Navbar variant={theme} expand="lg" className="mb-4">
        <Container>
          <Navbar.Brand as={Link} to="/">
            <FaCode className="me-2" /> CodeBin
          </Navbar.Brand>

          {/* 6. This is our new Theme Toggle Switch! */}
          <Form className="d-flex align-items-center">
            <FaSun className="me-2" style={{ color: theme === 'light' ? '#f39c12' : '#888' }} />
            <Form.Check
              type="switch"
              id="theme-switch"
              checked={theme === 'dark'}
              onChange={toggleTheme}
            />
            <FaMoon className="ms-2" style={{ color: theme === 'dark' ? '#3498db' : '#888' }} />
          </Form>

        </Container>
      </Navbar>

      <div className="app-container">
        <Routes>
          {/* 7. Pass the theme to our page components */}
          <Route path="/" element={<HomePage theme={theme} />} />
          <Route path="/snippet/:id" element={<SnippetPage theme={theme} />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
