import React, { useState, useEffect, useRef } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';
import { Card, Spinner, Alert, Button, Container, ListGroup } from 'react-bootstrap';
import Editor from '@monaco-editor/react';
import { FaPlus, FaPlayCircle, FaCheckCircle, FaTimesCircle } from 'react-icons/fa';

// Accept the 'theme' prop
const SnippetPage = ({ theme }) => {
  const { id } = useParams();
  const [snippet, setSnippet] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  
  const editorRef = useRef(null);
  const monacoRef = useRef(null);
  const [analysis, setAnalysis] = useState([]);

  useEffect(() => {
    const fetchSnippet = async () => {
      try {
        setLoading(true);
        setError('');
        const response = await axios.get(`http://localhost:5000/api/snippets/${id}`);
        setSnippet(response.data);
      } catch (err) {
        setError(err.response?.data?.message || 'Snippet not found.');
      } finally {
        setLoading(false);
      }
    };
    fetchSnippet();
  }, [id]);

  function handleEditorDidMount(editor, monaco) {
    editorRef.current = editor;
    monacoRef.current = monaco;
  }

  const handleAnalyzeCode = () => {
    if (!editorRef.current || !monacoRef.current) return;
    const monaco = monacoRef.current;
    const model = editorRef.current.getModel();
    const markers = monaco.editor.getModelMarkers({ resource: model.uri });

    if (markers.length === 0) {
      setAnalysis([{ 
        type: 'success', 
        message: 'No errors or warnings found. This code looks clean!' 
      }]);
      return;
    }

    const formattedSuggestions = markers.map(marker => {
      let customSuggestion = 'Review the error and your code logic.';
      if (marker.message.includes("';' expected")) {
        customSuggestion = `Suggestion: Try adding a semicolon ';' at the end of line ${marker.startLineNumber - 1}.`;
      }
      if (marker.message.includes("is not defined")) {
        customSuggestion = `Suggestion: Make sure the variable or function "${marker.message.split("'")[1]}" is defined, imported, and spelled correctly.`;
      }
      if (marker.message.includes("Unterminated string constant")) {
        customSuggestion = `Suggestion: Look for a missing closing quote (' or ") on line ${marker.startLineNumber}.`;
      }
      return {
        type: marker.severity === monaco.MarkerSeverity.Error ? 'danger' : 'warning',
        line: marker.startLineNumber,
        message: marker.message,
        suggestion: customSuggestion
      };
    });
    setAnalysis(formattedSuggestions);
  };

  // --- THESE BLOCKS MUST COME *BEFORE* THE MAIN RETURN ---
  if (loading) {
    return (
      <Container className="text-center mt-5">
        <Spinner animation="border" />
        <p>Loading Snippet...</p>
      </Container>
    );
  }
  if (error) {
    return (
      <Container className="mt-5">
        <Alert variant="danger"><h4>Error</h4><p>{error}</p><Button as={Link} to="/" variant="danger">Go Home</Button></Alert>
      </Container>
    );
  }

  // --- THIS IS THE MAIN (AND FINAL) RETURN STATEMENT ---
  return (
    <Container className="mt-4">
      <Button as={Link} to="/" variant="secondary" className="mb-3">
        <FaPlus className="me-2" />
        Create New Snippet
      </Button>
      
      {snippet && (
        <Card className="editor-card">
          <Card.Header>
            Language: <strong>{snippet.language}</strong> | Created: {new Date(snippet.createdAt).toLocaleString()}
          </Card.Header>
          <Card.Body>
            <Editor
              height="60vh"
              theme={theme === 'dark' ? 'vs-dark' : 'vs-light'}
              language={snippet.language}
              value={snippet.content}
              onMount={handleEditorDidMount}
              options={{
                readOnly: true,
                domReadOnly: true,
                minimap: { enabled: true }
              }}
            />
          </Card.Body>
          
          <Card.Footer>
            <Button variant="success" onClick={handleAnalyzeCode}>
              <FaPlayCircle className="me-2" />
              Analyze Code
            </Button>
            
            {analysis.length > 0 && (
              <div className="mt-4">
                <h4>Analysis Results:</h4>
                <ListGroup>
                  {analysis.map((item, index) => (
                    <ListGroup.Item 
                      key={index} 
                      className={theme === 'light' ? 'bg-light text-dark' : 'bg-dark text-light'}
                    >
                      {item.type === 'success' ? (
                        <strong className="text-success"><FaCheckCircle className="me-2" />{item.message}</strong>
                      ) : (
                        <>
                          <strong className={item.type === 'danger' ? 'text-danger' : 'text-warning'}>
                            <FaTimesCircle className="me-2" /> Line {item.line}:
                          </strong> {item.message}
                          <br />
                          <small>{item.suggestion}</small>
                        </>
                      )}
                    </ListGroup.Item>
                  ))}
                </ListGroup>
              </div>
            )}
          </Card.Footer>
        </Card>
      )}
    </Container>
  );
};

export default SnippetPage;
