import React, { useState } from 'react';
import { Form, Button, Card, Container, Spinner, Alert } from 'react-bootstrap';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // Hook for redirection

const HomePage = () => {
  const [content, setContent] = useState('');
  const [language, setLanguage] = useState('javascript');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const navigate = useNavigate(); // Get the navigate function

  const handleSubmit = async (e) => {
    e.preventDefault(); // Prevent form from reloading page
    setIsLoading(true);
    setError('');

    try {
      // Send the data to our API (running on port 5000)
      const response = await axios.post('http://localhost:5000/api/snippets', {
        content,
        language
      });

      // On success, redirect to the new snippet's page
      const { id } = response.data;
      navigate(`/snippet/${id}`);

    } catch (err) {
      setError(err.response?.data?.message || 'Failed to save snippet. Try again.');
      setIsLoading(false);
    }
  };

  return (
    <Container className="mt-4">
      <Card bg="light">
        <Card.Header as="h3" className="text-center">Create New Snippet</Card.Header>
        <Card.Body>
          <Form onSubmit={handleSubmit}>
            <Form.Group className="mb-3">
              <Form.Label>Language</Form.Label>
              <Form.Select 
                value={language} 
                onChange={(e) => setLanguage(e.target.value)}
              >
                <option value="javascript">JavaScript</option>
                <option value="python">Python</option>
                <option value="html">HTML</option>
                <option value="css">CSS</option>
                <option value="java">Java</option>
                <option value="plaintext">Plain Text</option>
              </Form.Select>
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Code</Form.Label>
              <Form.Control
                as="textarea"
                rows={18}
                value={content}
                onChange={(e) => setContent(e.target.value)}
                placeholder="Paste your code here..."
                required
                style={{ fontFamily: 'monospace' }} // Use a code-friendly font
              />
            </Form.Group>
            
            {error && <Alert variant="danger">{error}</Alert>}

            <Button variant="primary" type="submit" disabled={isLoading} className="w-100">
              {isLoading ? (
                <>
                  <Spinner as="span" animation="border" size="sm" role="status" aria-hidden="true" />
                  {' '} Saving...
                </>
              ) : (
                'Save & Share'
              )}
            </Button>
          </Form>
        </Card.Body>
      </Card>
    </Container>
  );
};

export default HomePage;
