import React, { useState } from 'react';
import { Form, Button, Card, Container, Spinner, Alert } from 'react-bootstrap';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import Editor from '@monaco-editor/react';

// 1. Import a save icon
import { FaSave } from 'react-icons/fa';

const HomePage = () => {
  const [content, setContent] = useState('');
  const [language, setLanguage] = useState('javascript');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setError('');

    try {
      const response = await axios.post('http://localhost:5000/api/snippets', {
        content,
        language
      });
      navigate(`/snippet/${response.data.id}`);
    } catch (err) {
      setError(err.response?.data?.message || 'Failed to save snippet. Try again.');
      setIsLoading(false);
    }
  };

  function handleEditorChange(value, event) {
    setContent(value);
  }

  return (
    <Container className="mt-4">
      {/* 2. Apply the dark theme to the card */}
      <Card bg="dark" text="light" border="secondary" className="editor-card">
        <Card.Header as="h3" className="text-center">Create New Snippet</Card.Header>
        <Card.Body>
          <Form onSubmit={handleSubmit}>
            <Form.Group className="mb-3">
              <Form.Label>Language</Form.Label>
              <Form.Select 
                value={language} 
                onChange={(e) => setLanguage(e.target.value)}
                // 3. Make the form select dark-themed too
                className="bg-dark text-light"
              >
                <option value="javascript">JavaScript</option>
                <option value="python">Python</option>
                <option value="html">HTML</option>
                <option value="css">CSS</option>
                <option value="java">Java</option>
                <option value="plaintext">Plain Text</option>
              </Form.Select>
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Code</Form.Label>
              {/* The editor border is now handled by our CSS on the div */}
              <div style={{ border: '1px solid #444', borderRadius: '.25rem', overflow: 'hidden' }}>
                <Editor
                  height="60vh"
                  theme="vs-dark"
                  language={language}
                  defaultValue="// Paste your code here... errors will be highlighted!"
                  onChange={handleEditorChange}
                  options={{ minimap: { enabled: false } }}
                />
              </div>
            </Form.Group>

            {error && <Alert variant="danger">{error}</Alert>}

            <Button variant="primary" type="submit" disabled={isLoading} className="w-100">
              {/* 4. Add the icon to the button */}
              <FaSave className="me-2" />
              {isLoading ? 'Saving...' : 'Save & Share'}
            </Button>
          </Form>
        </Card.Body>
      </Card>
    </Container>
  );
};

export default HomePage;
