import React, { useState, useEffect, useRef } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';
import hljs from 'highlight.js'; // Import highlight.js
import { Card, Spinner, Alert, Button, Container } from 'react-bootstrap';

const SnippetPage = () => {
  const { id } = useParams(); // Get the :id from the URL
  const [snippet, setSnippet] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const codeRef = useRef(null); // Create a "ref" to access the <code> DOM element

  useEffect(() => {
    // This effect runs once to fetch the snippet data
    const fetchSnippet = async () => {
      try {
        setLoading(true);
        setError('');
        const response = await axios.get(`http://localhost:5000/api/snippets/${id}`);
        setSnippet(response.data);
      } catch (err) {
        setError(err.response?.data?.message || 'Snippet not found.');
      } finally {
        setLoading(false);
      }
    };
    fetchSnippet();
  }, [id]); // Re-run if the 'id' in the URL changes

  useEffect(() => {
    // This effect runs *after* the snippet data is set
    if (snippet && codeRef.current) {
      // This is the magic!
      // It finds the 'codeRef' element and applies highlighting
      hljs.highlightElement(codeRef.current);
    }
  }, [snippet]); // Re-run whenever 'snippet' data changes

  if (loading) {
    return (
      <Container className="text-center mt-5">
        <Spinner animation="border" />
        <p>Loading Snippet...</p>
      </Container>
    );
  }

  if (error) {
    return (
      <Container className="mt-5">
        <Alert variant="danger">
          <h4>Error</h4>
          <p>{error}</p>
          <Button as={Link} to="/" variant="danger">Go Home</Button>
        </Alert>
      </Container>
    );
  }

  return (
    <Container className="mt-4">
      <Button as={Link} to="/" variant="secondary" className="mb-3">
        &larr; Create New Snippet
      </Button>
      <Card bg="dark" text="light">
        <Card.Header>
          Language: <strong>{snippet.language}</strong> | Created: {new Date(snippet.createdAt).toLocaleString()}
        </Card.Header>
        <Card.Body>
          {/* This <pre><code> structure is required by highlight.js */}
          <pre>
            <code 
              ref={codeRef} // Attach the ref here
              className={`language-${snippet.language}`}
            >
              {snippet.content}
            </code>
          </pre>
        </Card.Body>
      </Card>
    </Container>
  );
};

export default SnippetPage;
