import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';
import { Card, Spinner, Alert, Button, Container } from 'react-bootstrap';

// 1. Import the Monaco Editor
import Editor from '@monaco-editor/react';

const SnippetPage = () => {
  const { id } = useParams();
  const [snippet, setSnippet] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // We no longer need useRef or the hljs useEffect

  useEffect(() => {
    const fetchSnippet = async () => {
      try {
        setLoading(true);
        setError('');
        const response = await axios.get(`http://localhost:5000/api/snippets/${id}`);
        setSnippet(response.data);
      } catch (err) {
        setError(err.response?.data?.message || 'Snippet not found.');
      } finally {
        setLoading(false);
      }
    };
    fetchSnippet();
  }, [id]);


  if (loading) {
    return (
      <Container className="text-center mt-5">
        <Spinner animation="border" />
        <p>Loading Snippet...</p>
      </Container>
    );
  }

  if (error) {
    return (
      <Container className="mt-5">
        <Alert variant="danger">
          <h4>Error</h4>
          <p>{error}</p>
          <Button as={Link} to="/" variant="danger">Go Home</Button>
        </Alert>
      </Container>
    );
  }

  return (
    <Container className="mt-4">
      <Button as={Link} to="/" variant="secondary" className="mb-3">
        &larr; Create New Snippet
      </Button>
      <Card>
        <Card.Header>
          Language: <strong>{snippet.language}</strong> | Created: {new Date(snippet.createdAt).toLocaleString()}
        </Card.Header>
        <Card.Body>
          {/* 2. Replace the <pre><code> block with the Editor */}
          <Editor
            height="60vh"
            theme="vs-dark"
            language={snippet.language}
            value={snippet.content} // Use 'value' to set the content
            options={{
              readOnly: true, // Make it read-only
              domReadOnly: true,
              minimap: { enabled: true } // Show the minimap on this page
            }}
          />
        </Card.Body>
      </Card>
    </Container>
  );
};

export default SnippetPage;
