import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';
import { Card, Spinner, Alert, Button, Container } from 'react-bootstrap';
import Editor from '@monaco-editor/react';
import { FaPlus } from 'react-icons/fa';

const SnippetPage = () => {
  const { id } = useParams();
  const [snippet, setSnippet] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchSnippet = async () => {
      try {
        setLoading(true);
        setError('');
        const response = await axios.get(`http://localhost:5000/api/snippets/${id}`);
        setSnippet(response.data);
      } catch (err) {
        setError(err.response?.data?.message || 'Snippet not found.');
      } finally {
        setLoading(false);
      }
    };
    fetchSnippet();
  }, [id]);

  // --- FIX IS HERE ---
  // We must RETURN the loading spinner.
  if (loading) {
    return (
      <Container className="text-center mt-5">
        <Spinner animation="border" />
        <p>Loading Snippet...</p>
      </Container>
    );
  }

  // --- AND FIX IS HERE ---
  // We must also RETURN the error alert.
  if (error) {
    return (
      <Container className="mt-5">
        <Alert variant="danger">
          <h4>Error</h4>
          <p>{error}</p>
          <Button as={Link} to="/" variant="danger">Go Home</Button>
        </Alert>
      </Container>
    );
  }

  // If the code reaches this point, we know:
  // 1. loading is false.
  // 2. error is empty.
  // 3. snippet MUST be loaded.
  // This makes the rest of the code safe.
  return (
    <Container className="mt-4">
      <Button as={Link} to="/" variant="secondary" className="mb-3">
        <FaPlus className="me-2" />
        Create New Snippet
      </Button>
      
      {/* We add this extra check for safety, in case snippet is still null */}
      {snippet && (
        <Card bg="dark" text="light" border="secondary" className="editor-card">
          <Card.Header>
            Language: <strong>{snippet.language}</strong> | Created: {new Date(snippet.createdAt).toLocaleString()}
          </Card.Header>
          <Card.Body>
            <Editor
              height="60vh"
              theme="vs-dark"
              language={snippet.language}
              value={snippet.content}
              options={{
                readOnly: true,
                domReadOnly: true,
                minimap: { enabled: true }
              }}
            />
          </Card.Body>
        </Card>
      )}
    </Container>
  );
};

export default SnippetPage;
