import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';
import { Card, Spinner, Alert, Button, Container } from 'react-bootstrap';
import Editor from '@monaco-editor/react';

// 1. Import a "plus" icon
import { FaPlus } from 'react-icons/fa';

const SnippetPage = () => {
  const { id } = useParams();
  const [snippet, setSnippet] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchSnippet = async () => {
      try {
        setLoading(true);
        setError('');
        const response = await axios.get(`http://localhost:5000/api/snippets/${id}`);
        setSnippet(response.data);
      } catch (err) {
        setError(err.response?.data?.message || 'Snippet not found.');
      } finally {
        setLoading(false);
      }
    };
    fetchSnippet();
  }, [id]);

  if (loading) {
    // ... (loading spinner remains the same)
  }

  if (error) {
    // ... (error alert remains the same)
  }

  return (
    <Container className="mt-4">
      <Button as={Link} to="/" variant="secondary" className="mb-3">
        {/* 2. Add icon to the button */}
        <FaPlus className="me-2" />
        Create New Snippet
      </Button>

      {/* 3. Apply dark theme to the card */}
      <Card bg="dark" text="light" border="secondary" className="editor-card">
        <Card.Header>
          Language: <strong>{snippet.language}</strong> | Created: {new Date(snippet.createdAt).toLocaleString()}
        </Card.Header>
        <Card.Body>
          <Editor
            height="60vh"
            theme="vs-dark"
            language={snippet.language}
            value={snippet.content}
            options={{
              readOnly: true,
              domReadOnly: true,
              minimap: { enabled: true }
            }}
          />
        </Card.Body>
      </Card>
    </Container>
  );
};

export default SnippetPage;
