import React, { useState, useEffect, useRef } from 'react';
import { useParams, Link } from 'react-router-dom';
import axios from 'axios';
import { Card, Spinner, Alert, Button, Container, ListGroup } from 'react-bootstrap';
// Import useMonaco hook
import Editor, { useMonaco } from '@monaco-editor/react';
import { toast } from 'react-hot-toast';
import { CopyToClipboard } from 'react-copy-to-clipboard';
import { FaPlus, FaPlayCircle, FaCheckCircle, FaTimesCircle, FaCopy } from 'react-icons/fa';

const SnippetPage = ({ theme }) => {
  const { id } = useParams();
  const [snippet, setSnippet] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  const editorRef = useRef(null);
  // Get the monaco instance via hook
  const monaco = useMonaco();
  const [analysis, setAnalysis] = useState([]);
  const [isAnalyzing, setIsAnalyzing] = useState(false);

  useEffect(() => {
    // ... (fetchSnippet logic remains the same)
    const fetchSnippet = async () => {
      try {
        setLoading(true); setError('');
        const response = await axios.get(`http://localhost:5000/api/snippets/${id}`);
        setSnippet(response.data);
      } catch (err) {
        setError(err.response?.data?.message || 'Snippet not found.');
      } finally {
        setLoading(false);
      }
    };
    fetchSnippet();
  }, [id]);

  // Store editor instance when mounted
  function handleEditorDidMount(editor, _theMonacoInstance) {
    editorRef.current = editor;
  }

  // --- Helper function to process analysis results ---
  const processMarkers = (markers, isPython = false) => {
    // Check monaco instance before accessing MarkerSeverity
    const severityError = monaco ? monaco.MarkerSeverity.Error : 8; // Default severity if monaco is not ready

    if (!markers || markers.length === 0) {
      setAnalysis([{ type: 'success', message: 'No errors or warnings found. This code looks clean!' }]);
    } else {
      const formattedSuggestions = markers.map(marker => {
        if (!isPython) {
          // Format Monaco/TypeScript diagnostics
          let customSuggestion = 'Review the error and your code logic.';
          const messageText = typeof marker.messageText === 'string' ? marker.messageText : marker.message || "";

          if (messageText.includes("';' expected")) { customSuggestion = `Suggestion: Try adding a semicolon ';' at the end of line ${marker.startLineNumber - 1}.`; }
          if (messageText.includes("is not defined")) { customSuggestion = `Suggestion: Make sure the variable or function "${messageText.split("'")[1]}" is defined, imported, and spelled correctly.`; }
          if (messageText.includes("Expected corresponding closing tag")) { const tagName = messageText.split("'")[1]; customSuggestion = `Suggestion: The <${tagName}> tag on line ${marker.startLineNumber} is not closed. Try adding a </${tagName}> tag.`; }
          if (messageText.includes("Cannot assign to") && messageText.includes("constant")) {
             customSuggestion = `Suggestion: You declared this variable with 'const', which means it cannot be reassigned. Use 'let' instead if you need to change its value.`;
          }

          return {
            type: (marker.category === 1 || marker.severity === severityError) ? 'danger' : 'warning',
            line: marker.startLineNumber || (marker.start?.line || 0) + 1,
            message: messageText,
            suggestion: customSuggestion
          };
        } else {
          // Format Python errors
          let customSuggestion = `Suggestion: ${marker.message}.`;
          if (marker.message.includes("Invalid syntax")) { customSuggestion = `Suggestion: ${marker.message}. Check for typos (like 'defs' instead of 'def'), missing colons, or incorrect indentation.`; }
          return { type: 'danger', line: marker.line, message: marker.message, suggestion: customSuggestion };
        }
      });
      setAnalysis(formattedSuggestions);
    }
    setIsAnalyzing(false);
  };

  // --- Final Analyze Function ---
  const handleAnalyzeCode = async () => {
    // **CRITICAL FIX**: Check if monaco instance from the hook is ready
    if (!editorRef.current || !monaco) {
      toast.error("Editor services are not ready yet. Please wait a moment and try again.");
      setIsAnalyzing(false); // Make sure button is re-enabled
      return;
    }

    setIsAnalyzing(true);
    setAnalysis([]);

    const language = snippet.language;
    const model = editorRef.current.getModel();
    if (!model) { setIsAnalyzing(false); return; }
    const modelUri = model.uri;

    // --- Python Analysis ---
    if (language === 'python') {
      try {
        const response = await axios.post('http://localhost:5000/api/analyze/python', { code: snippet.content });
        processMarkers(response.data, true);
      } catch (err) {
        setAnalysis([{ type: 'danger', message: 'Error running Python analyzer on server.' }]);
        setIsAnalyzing(false);
      }
    }
    // --- JavaScript/TypeScript Analysis ---
    else if (language === 'javascript' || language === 'typescript') {
      try {
        const worker = await monaco.languages.typescript.getTypeScriptWorker();
        const client = await worker(modelUri);
        const diagnostics = await Promise.all([
          client.getSyntacticDiagnostics(modelUri.toString()),
          client.getSemanticDiagnostics(modelUri.toString())
        ]);
        const allDiagnostics = diagnostics.flat();

        // Convert TS Diagnostic objects
        const formattedMarkers = allDiagnostics.map(diag => {
            // Check if diag.start exists and has line property before accessing it
            const startLine = diag.start !== undefined && diag.start !== null ? model.getPositionAt(diag.start).lineNumber : 1; // Default to line 1 if start is missing
            return {
                startLineNumber: startLine,
                messageText: typeof diag.messageText === 'object'
                             ? diag.messageText.messageText // Handle nested messageText
                             : diag.messageText,
                category: diag.category,
                severity: diag.category === 1 ? monaco.MarkerSeverity.Error : monaco.MarkerSeverity.Warning
            };
        });
        processMarkers(formattedMarkers, false);

      } catch (tsError) {
          console.error("Error getting TS diagnostics:", tsError);
          // Fallback to getModelMarkers if worker fails
          editorRef.current.focus();
          setTimeout(() => {
              const markers = monaco.editor.getModelMarkers({ resource: modelUri });
              processMarkers(markers, false);
          }, 300);
      }
    }
    // --- Fallback for HTML/CSS/Other ---
    else {
      editorRef.current.focus();
      setTimeout(() => {
        const markers = monaco.editor.getModelMarkers({ resource: modelUri });
        processMarkers(markers, false);
      }, 300);
    }
  };

  const onCopyCode = () => {
    toast.success('Code copied to clipboard!');
  };

  // --- Loading and Error states ---
  if (loading) { return ( <Container className="text-center mt-5"><Spinner animation="border" /><p>Loading Snippet...</p></Container> ); }
  if (error) { return ( <Container className="mt-5"><Alert variant="danger"><h4>Error</h4><p>{error}</p><Button as={Link} to="/" variant="danger">Go Home</Button></Alert></Container> ); }

  return (
    <Container fluid className="mb-4">
      {/* ... Buttons ... */}
      <Button as={Link} to="/" variant="secondary" className="mb-3">
        <FaPlus className="me-2" /> Create New Snippet
      </Button>
      {snippet && (
        <Card className="editor-card">
          <Card.Header>
            Language: <strong>{snippet.language}</strong> | Created: {new Date(snippet.createdAt).toLocaleString()}
          </Card.Header>
          <Card.Body>
            <Editor
              height="60vh"
              theme={theme === 'dark' ? 'vs-dark' : 'vs-light'}
              language={snippet.language}
              value={snippet.content}
              onMount={handleEditorDidMount} // Keep this
              options={{
                readOnly: true, domReadOnly: true, minimap: { enabled: true }, validate: true
              }}
            />
          </Card.Body>
          <Card.Footer>
            <div className="d-flex align-items-center">
              {/* Analyze Button */}
              <Button variant="success" onClick={handleAnalyzeCode} className="py-2" disabled={isAnalyzing}>
                 {isAnalyzing ? ( <Spinner as="span" animation="border" size="sm" /> ) : ( <FaPlayCircle className="me-2" /> )} Analyze Code
              </Button>
              {/* Copy Button */}
              <CopyToClipboard text={snippet.content} onCopy={onCopyCode}>
                <Button variant="primary" className="ms-2 py-2"> <FaCopy className="me-2" /> Copy Code </Button>
              </CopyToClipboard>
            </div>

            {/* --- THIS IS THE CORRECTED SECTION --- */}
            {analysis.length > 0 && (
              <div className="mt-4">
                <h4>Analysis Results:</h4>
                <ListGroup className="analysis-list">
                  {analysis.map((item, index) => (
                    <ListGroup.Item
                      key={index}
                      className={theme === 'light' ? 'bg-light text-dark' : 'bg-dark text-light'}
                    >
                      {item.type === 'success' ? (
                        <strong className="text-success"><FaCheckCircle className="me-2" />{item.message}</strong>
                      ) : (
                        <>
                          <strong className={item.type === 'danger' ? 'text-danger' : 'text-warning'}>
                            <FaTimesCircle className="me-2" /> Line {item.line}:
                          </strong> {item.message}
                          <br />
                          <small>{item.suggestion}</small>
                        </>
                      )}
                    </ListGroup.Item>
                  ))}
                </ListGroup>
              </div>
            )}
            {/* --- END OF CORRECTED SECTION --- */}

          </Card.Footer>
        </Card>
      )}
    </Container>
  );
};

export default SnippetPage;
